mapper.select:
SELECT DISTINCT TRIM(id) AS class_a, TRIM(name) AS class_a_name FROM wh_fault_class
WHERE type='A'
AND create_dept = #{create_dept}
AND id = #{class_a}
ORDER BY class_a_name

SELECT DISTINCT TRIM(id) AS class_b, TRIM(name) AS class_b_name FROM wh_fault_class
WHERE type='B' AND pid = #{class_a}
AND id = #{class_b}
